import React from 'react';
import classes from './AddClientCockpit.module.css';

const addClientCockpit = (props) => {

    return (
        <div>
            <h2 className = {classes.Header}>Add a Client</h2>
            <form className={classes.Input}>
                {props.newClient.map((client,index) => {
                    return(
                        <div className={classes.InputItem}>
                            <div> <label key = {index}> {client.name}: </label> </div>
                            <div> <input type="text" onChange = {(event) => props.newInfoHandle(event, client.id)} placeholder={props.newClient[index].placeHolder}/> </div>    
                        </div>

                    )
                })}
            </form>
            <div className={classes.ButtonArea}>
                <button className  = {classes.Button} onClick = {props.clicked}>Submit</button>
                <button className = {classes.Button} onClick = {props.mainRender}>Go Back</button>
            </div>
        </div>
    );
}

export default addClientCockpit;
